mapper.insert:

    INSERT INTO CERTIFICATION (PARTICIPATION_ID
						    , COMMENT
						    , IMAGE_URL
						    , LIKE_COUNT
						    , CREATED_BY
    					      ) 
    VALUES (#{participationId}
         , #{comment}
         , #{imageUrl}
         , #{likeCount}
         , #{createdBy}
    )

mapper.update:

UPDATE CERTIFICATION
   SET COMMENT = #{comment}
     , IMAGE_URL = #{imageUrl}
     , MODIFIED_DATE = NOW()
 WHERE CERTIFICATION_ID = #{certificationId}
   AND CREATED_BY = #{userId}

mapper.delete:

DELETE 
  FROM CERTIFICATION
 WHERE CERTIFICATION_ID = #{certificationId}
   AND CREATED_BY = #{userId}
