mapper.insert:
INSERT INTO CHALLENGE (TITLE
					, DESCRIPTION
					, START_DATE
					, END_DATE
					, CATEGORY_ID
					, CREATED_BY)
VALUES (#{title}
	 , #{description}
	 , #{startDate}
	 , #{endDate}
	 , #{categoryId}
	 , #{createdBy})

mapper.select:
SELECT C.CHALLENGE_ID AS challengeId , C.TITLE AS title , C.DESCRIPTION AS description , C.CATEGORY_ID AS categoryId , C.CREATED_BY AS createdBy , C.START_DATE AS startDate , C.END_DATE AS endDate , C.CREATED_DATE createdDate , C.MODIFIED_DATE modifiedDate , CASE WHEN FC.USER_ID IS NOT NULL THEN TRUE ELSE FALSE END AS isFavorite , U.USER_ID AS userId , U.NICKNAME AS nickname FROM CHALLENGE C LEFT JOIN FAVORITE_CHALLENGE FC ON C.CHALLENGE_ID = FC.CHALLENGE_ID AND FC.USER_ID = #{userId} INNER JOIN USER U ON C.CREATED_BY = U.USER_ID
(C.IS_DELETED = 0 OR C.IS_DELETED IS NULL)
AND C.TITLE LIKE CONCAT('%', #{search}, '%')
AND C.CATEGORY_ID = #{categoryId}
ORDER BY C.CREATED_DATE DESC LIMIT #{size} OFFSET #{offset}
