mapper.select:
SELECT COUNT(*) > 0 
  FROM USER 
 WHERE NICKNAME = #{nickname}

   AND USER_ID != #{excludeUserId}

mapper.update:
UPDATE USER 
   SET NICKNAME = #{nickname}
     , MODIFIED_DATE = CURRENT_TIMESTAMP
 WHERE USER_ID = #{userId}

UPDATE USER 
   SET PASSWORD = #{password}
     , MODIFIED_DATE = CURRENT_TIMESTAMP
 WHERE USER_ID = #{userId}
